class CookieJar{constructor(){}set(e,t,o){var i="";if(o){var s=new Date;s.setTime(s.getTime()+24*o*60*60*1e3),i="; expires="+s.toUTCString()}document.cookie=e+"="+(t||"")+i+"; path=/"}get(e){for(var t=e+"=",o=document.cookie.split(";"),i=0;i<o.length;i++){for(var s=o[i];" "==s.charAt(0);)s=s.substring(1,s.length);if(0==s.indexOf(t))return s.substring(t.length,s.length)}return null}erase(e){document.cookie=e+"=; Max-Age=-99999999"}}class Alert{constructor(e,t){this.alert=document.querySelector(e),this.closeButton=this.alert.querySelector(".alert-close"),this.type=this.alert.dataset.type,this.cookieID=this.alert.id,this.cookies=t,this.init()}open(){this.alert.dataset.open=!0}close(){this.alert.dataset.open=!1,this.cookies.set("alert-"+this.cookieID,"hide",0)}init(){this.closeButton.addEventListener("click",()=>{this.close()}),this.cookies.get("alert-"+this.cookieID)||this.cookies.set("alert-"+this.cookieID,"show",0),"show"==this.cookies.get("alert-"+this.cookieID)?this.open():this.close()}}const cookies=new CookieJar;if(document.getElementById("challenge")){new Alert("#challenge",cookies)}if(document.getElementById("cheers")){new Alert("#cheers",cookies)}document.querySelector(".glide")&&new Glide(".glide").mount();